import React, { useState } from 'react';
import {
  Container,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  Grid,
  Box,
  Card,
  CardContent,
  CardActions,
} from '@mui/material';

function Comments() {
  const [comments, setComments] = useState([
    {
      id: 1,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 2,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 3,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 4,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 5,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 6,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 7,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 8,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 9,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 10,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 11,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 12,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
  ]);

  const handlePublish = (commentId) => {
    const updatedComments = comments.filter(
      (comment) => comment.id !== commentId
    );
    setComments(updatedComments);
    // Despues hay que agregar la logica de publicar el comentario, ahora solo desaparce de la pag (leido)
  };

  const handleDelete = (commentId) => {
    const updatedComments = comments.filter(
      (comment) => comment.id !== commentId
    );
    setComments(updatedComments);
  };

  return (
    <Container>
      <Box sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Comentarios
        </Typography>
        <List>
          {comments.map((comment) => (
            <ListItem key={comment.id} disablePadding>
              <Card sx={{ width: '100%', mb: 2 }}>
                <CardContent>
                  <ListItemText
                    primary={comment.user}
                    secondary={
                      <>
                        <Typography
                          component="span"
                          variant="body2"
                          color="textPrimary"
                          sx={{ display: 'block' }}
                        >
                          {comment.comment}
                        </Typography>
                        {comment.secondaryComment}
                      </>
                    }
                  />
                </CardContent>
                <CardActions>
                  <Grid container spacing={2}>
                    <Grid item xs={12} md={6}>
                      <Button
                        variant="contained"
                        color="primary"
                        fullWidth
                        onClick={() => handlePublish(comment.id)}
                      >
                        Publicar
                      </Button>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Button
                        variant="contained"
                        color="secondary"
                        fullWidth
                        onClick={() => handleDelete(comment.id)}
                      >
                        Borrar
                      </Button>
                    </Grid>
                  </Grid>
                </CardActions>
              </Card>
            </ListItem>
          ))}
        </List>
      </Box>
    </Container>
  );
}

export default Comments;
