import React, { useState } from 'react';
import {
  Button,
  Container,
  Typography,
  Grid,
  Link,
  Avatar,
  Card,
  CardContent,
  TextField,
} from '@mui/material';

function ProviderProfile() {
  // Estados para los campos editables
  const [name, setName] = useState('Juan Pérez');
  const [title, setTitle] = useState('Profesor de Matemáticas');
  const [about, setAbout] = useState(
    'Soy profesor de matemáticas con más de 10 años de experiencia. He trabajado en diversas instituciones y ofrezco clases particulares a estudiantes de todos los niveles.'
  );

  return (
    <Container sx={{ flexGrow: 1, padding: 3 }}>
      <Typography variant="h4" gutterBottom>
        Perfil del Proveedor
      </Typography>
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={12} sm={4} md={3}>
          <Avatar
            alt="Proveedor"
            src="/path-to-image.jpg"
            sx={{ width: 10, height: 10 }}
          />
          {/* TODO: Añadir funcionalidad para cambiar la foto */}
          <Button variant="text" color="primary">
            Cambiar foto
          </Button>
        </Grid>
        <Grid item xs={12} sm={8} md={9}>
          <TextField
            fullWidth
            label="Nombre"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <TextField
            fullWidth
            label="Título"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </Grid>
      </Grid>
      <Card sx={{ marginTop: 2 }}>
        <CardContent>
          <Typography variant="h6">Sobre mí:</Typography>
          <TextField
            fullWidth
            multiline
            rows={4}
            variant="outlined"
            value={about}
            onChange={(e) => setAbout(e.target.value)}
          />
        </CardContent>
      </Card>
      <Grid container spacing={2}>
        <Grid item>
          <Button
            variant="contained"
            color="primary"
            sx={{ margin: 1 }}
            // TODO: Añadir funcionalidad para guardar cambios
          >
            Guardar cambios
          </Button>
        </Grid>
        <Grid item>
          <Button
            variant="contained"
            color="default"
            sx={{ margin: 1 }}
            component={Link}
            href="/"
          >
            Volver al inicio
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
}

export default ProviderProfile;
