import React, { useState } from 'react';
import {
  Container,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  Grid,
  Box,
} from '@mui/material';

function Comments() {
  const [comments, setComments] = useState([
    {
      id: 1,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    // ... (you can add more comments here)
  ]);

  const handlePublish = (commentId) => {
    console.log(`Publishing comment with ID: ${commentId}`);
    // Add logic to publish the comment
  };

  const handleDelete = (commentId) => {
    const updatedComments = comments.filter(comment => comment.id !== commentId);
    setComments(updatedComments);
  };

  return (
    <Container>
      <Box sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Comentarios
        </Typography>
        <List>
          {comments.map(comment => (
            <ListItem key={comment.id}>
              <ListItemText
                primary={comment.user}
                secondary={
                  <>
                    <Typography
                      component="span"
                      variant="body2"
                      color="textPrimary"
                      sx={{ display: 'block' }}
                    >
                      {comment.comment}
                    </Typography>
                    {comment.secondaryComment}
                  </>
                }
              />
              <Grid container spacing={2}>
                <Grid item>
                  <Button 
                    variant="contained" 
                    color="primary"
                    onClick={() => handlePublish(comment.id)}
                  >
                    Publicar
                  </Button>
                </Grid>
                <Grid item>
                  <Button 
                    variant="contained" 
                    color="secondary"
                    onClick={() => handleDelete(comment.id)}
                  >
                    Borrar
                  </Button>
                </Grid>
              </Grid>
            </ListItem>
          ))}
        </List>
      </Box>
    </Container>
  );
}

export default Comments;
