import React, { useState } from 'react';
import {
  Button,
  Container,
  Typography,
  Grid,
  Link,
  Avatar,
  Card,
  CardContent,
  TextField,
} from '@mui/material';
import makeStyles from '@mui/styles/makeStyles';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  button: {
    margin: theme.spacing(1),
  },
  largeAvatar: {
    width: theme.spacing(10),
    height: theme.spacing(10),
  },
  card: {
    marginTop: theme.spacing(2),
  },
}));

function ProviderProfile() {
  const classes = useStyles();

  // Estados para los campos editables
  const [name, setName] = useState('Juan Pérez');
  const [title, setTitle] = useState('Profesor de Matemáticas');
  const [about, setAbout] = useState(
    'Soy profesor de matemáticas con más de 10 años de experiencia. He trabajado en diversas instituciones y ofrezco clases particulares a estudiantes de todos los niveles.'
  );

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom>
        Perfil del Proveedor
      </Typography>
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={12} sm={4} md={3}>
          <Avatar
            alt="Proveedor"
            src="/path-to-image.jpg"
            className={classes.largeAvatar}
          />
          {/* TODO: Añadir funcionalidad para cambiar la foto */}
          <Button variant="text" color="primary">
            Cambiar foto
          </Button>
        </Grid>
        <Grid item xs={12} sm={8} md={9}>
          <TextField
            fullWidth
            label="Nombre"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <TextField
            fullWidth
            label="Título"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </Grid>
      </Grid>
      <Card className={classes.card}>
        <CardContent>
          <Typography variant="h6">Sobre mí:</Typography>
          <TextField
            fullWidth
            multiline
            rows={4}
            variant="outlined"
            value={about}
            onChange={(e) => setAbout(e.target.value)}
          />
        </CardContent>
      </Card>
      <Grid container spacing={2}>
        <Grid item>
          <Button
            variant="contained"
            color="primary"
            className={classes.button}
            // TODO: Añadir funcionalidad para guardar cambios
          >
            Guardar cambios
          </Button>
        </Grid>
        <Grid item>
          <Button
            variant="contained"
            color="default"
            className={classes.button}
            component={Link}
            href="/"
          >
            Volver al inicio
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
}

export default ProviderProfile;
