import React from 'react';
import {
  Typography,
  Container,
  Button,
  Card,
  CardContent,
  CardMedia,
  Grid,
} from '@mui/material';
import makeStyles from '@mui/styles/makeStyles';
import mockServices from '../data/mockServices';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  mainContent: {
    padding: theme.spacing(5),
    textAlign: 'center',
  },
  footer: {
    marginTop: theme.spacing(5),
    padding: theme.spacing(3),
    backgroundColor: '#f5f5f5',
  },
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
}));

function LandingPage() {
  const classes = useStyles();

  // Ejemplo de servicios mejor puntuados (esto debería obtenerse de una API o base de datos)
  const topRatedServices = [
    {
      title: 'Tutoría de Matemáticas',
      image: 'path_to_image1.jpg',
      rating: 4.8,
    },
    { title: 'Clases de Guitarra', image: 'path_to_image2.jpg', rating: 4.9 },
    {
      title: 'Consultoría de Marketing',
      image: 'path_to_image3.jpg',
      rating: 4.7,
    },
  ];

  return (
    <div className={classes.root}>
      <Container className={classes.mainContent}>
        <Typography variant="h4" gutterBottom>
          Bienvenido a ServiceHub
        </Typography>
        <Typography variant="h6" paragraph>
          Encuentra y ofrece servicios de calidad en nuestra plataforma. Desde
          tutorías hasta clases de música, ¡tenemos algo para todos!
        </Typography>
        <Button variant="contained" color="primary" size="large">
          Explorar Servicios
        </Button>

        <Typography variant="h5" gutterBottom style={{ marginTop: '40px' }}>
          Servicios Mejor Puntuados
        </Typography>
        <Grid container spacing={3}>
          {topRatedServices.map((service) => (
            <Grid item xs={12} sm={4} key={mockServices.id}>
              <Card className={classes.card}>
                <CardMedia
                  className={classes.media}
                  image={service.image}
                  title={service.title}
                />
                <CardContent>
                  <Typography variant="h6" component="div">
                    {service.title}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    Puntuación: {service.rating}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          ServiceHub
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary">
          Contacto: info@servicehub.com
        </Typography>
      </footer>
    </div>
  );
}

export default LandingPage;
