import React from 'react';
import {
  Typography,
  Container,
  Button,
  Card,
  CardContent,
  CardMedia,
  Grid,
} from '@mui/material';
import makeStyles from '@mui/styles/makeStyles';
import mockServices from '../data/mockServices';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  mainContent: {
    padding: theme.spacing(5),
    textAlign: 'center',
  },
  footer: {
    marginTop: theme.spacing(5),
    padding: theme.spacing(3),
    backgroundColor: '#f5f5f5',
  },
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
}));

function LandingPage() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Container className={classes.mainContent}>
        <Typography variant="h3" gutterBottom>
          ¡Descubre, Aprende, Crece!
        </Typography>
        <Typography variant="h5" paragraph>
          Bienvenido a Brainify, tu puerta de entrada a un mundo de oportunidades y aprendizaje.
        </Typography>
        <Typography variant="h6" paragraph>
          ¿Listo para explorar?
        </Typography>
        <Button variant="contained" color="primary" size="large">
          ¡Empieza Ahora!
        </Button>

        <Typography variant="h5" gutterBottom style={{ marginTop: '40px' }}>
          Echa un vistazo a algunos de nuestros servicios más populares
        </Typography>
        <Grid container spacing={3}>
          {mockServices.map((service) => (
            <Grid item xs={12} sm={4} key={service.id}>
              <Card className={classes.card}>
                <CardMedia
                  className={classes.media}
                  image={service.image} // Asumiendo que mockServices tiene un campo 'image' para cada servicio
                  title={service.title}
                />
                <CardContent>
                  <Typography variant="h6" component="div">
                    {service.title}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    {service.description} // Asumiendo que mockServices tiene un campo 'description' para cada servicio
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          Brainify
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary">
          Contacto: info@brainify.com
        </Typography>
      </footer>
    </div>
  );
}

export default LandingPage;
