import React, { useState } from 'react';
import {
  Container,
  Typography,
  Button,
  Card,
  CardContent,
  CardActions,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
  IconButton,
} from '@mui/material';
import { makeStyles } from '@mui/styles';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import mockServices from '../data/mockServices';
import NotificationGreen from '../context/NotificationGreen';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  button: {
    margin: theme.spacing(1),
  },
  card: {
    margin: theme.spacing(2),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
}));

function MyServices() {
  const classes = useStyles();
  const [services, setServices] = useState(mockServices);
  const [currentService, setCurrentService] = useState(null);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [formData, setFormData] = useState({
    nombre: '',
    categoria: '',
    tipo: '',
    duracion: '',
    frecuencia: '',
    costo: '',
  });
  const [notificationOpen, setNotificationOpen] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSave = () => {
    if (Object.values(formData).some((value) => !value)) {
      setErrorMessage('Por favor, completa todos los campos del formulario.');
      return;
    }

    const newService = {
      id: currentService ? currentService.id : Date.now(),
      ...formData,
    };

    if (currentService) {
      setServices((prevServices) =>
        prevServices.map((service) =>
          service.id === currentService.id ? newService : service
        )
      );
      setNotificationMessage('InformaciÃ³n actualizada correctamente');
    } else {
      setServices((prevServices) => [...prevServices, newService]);
      setNotificationMessage('Servicio agregado correctamente');
    }

    setCurrentService(null);
    setNotificationOpen(true);
    setFormData({
      serviceName: '',
      serviceCategory: '',
      serviceType: '',
      duration: '',
      frequency: '',
      cost: '',
    });
    setDialogOpen(false);
  };

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom>
        Mis Servicios
      </Typography>
      <Button
        variant="contained"
        color="primary"
        startIcon={<AddIcon />}
        onClick={() => setDialogOpen(true)}
      >
        Agregar Servicio
      </Button>
      <Grid container spacing={3}>
        {services.map((service) => (
          <Grid item xs={12} sm={6} md={4} key={service.id}>
            <Card className={classes.card}>
              <CardContent>
                <Typography variant="h6">{service.nombre}</Typography>
                <Typography variant="body2">
                  DuraciÃ³n: {service.duracion}
                </Typography>
                <Typography variant="body2">Costo: {service.costo}</Typography>
              </CardContent>
              <CardActions>
                <IconButton
                  onClick={() => {
                    setCurrentService(service);
                    setFormData({
                      nombre: service.nombre,
                      categoria: service.categoria,
                      tipo: service.tipo,
                      duracion: service.duracion,
                      frecuencia: service.frecuencia,
                      costo: service.costo,
                    });
                    setDialogOpen(true);
                  }}
                >
                  <EditIcon />
                </IconButton>
                <IconButton
                  onClick={() => {
                    setServices((prevServices) =>
                      prevServices.filter((s) => s.id !== service.id)
                    );
                    setNotificationMessage('Servicio eliminado correctamente');
                    setNotificationOpen(true);
                  }}
                >
                  <DeleteIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
        <DialogTitle>
          {currentService ? 'Modificar Servicio' : 'Agregar Servicio'}
        </DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            margin="normal"
            label="Nombre"
            name="nombre"
            value={formData.nombre}
            onChange={handleInputChange}
          />
          <FormControl className={classes.formControl}>
            <InputLabel>CategorÃ­a</InputLabel>
            <Select
              name="categoria"
              value={formData.categoria}
              onChange={handleInputChange}
            >
              <MenuItem value="tutorias">TutorÃ­as escolares</MenuItem>
              <MenuItem value="idioma">Clases de idioma</MenuItem>
            </Select>
          </FormControl>
          <FormControl className={classes.formControl}>
            <InputLabel>Tipo de clase</InputLabel>
            <Select
              name="tipo"
              value={formData.tipo}
              onChange={handleInputChange}
            >
              <MenuItem value="individual">Individual</MenuItem>
              <MenuItem value="grupal">Grupal</MenuItem>
            </Select>
          </FormControl>
          <TextField
            fullWidth
            margin="normal"
            label="DuraciÃ³n (En Semanas)"
            name="duracion"
            value={formData.duracion}
            onChange={handleInputChange}
          />
          <FormControl className={classes.formControl}>
            <InputLabel>Frecuencia</InputLabel>
            <Select
              name="frecuencia"
              value={formData.frecuencia}
              onChange={handleInputChange}
            >
              <MenuItem value="Ãºnica">Ãnica</MenuItem>
              <MenuItem value="semanal">Semanal</MenuItem>
              <MenuItem value="mensual">Mensual</MenuItem>
            </Select>
          </FormControl>
          <TextField
            fullWidth
            margin="normal"
            label="Costo"
            name="costo"
            value={formData.costo}
            onChange={handleInputChange}
          />
          {errorMessage && (
            <Typography color="error">{errorMessage}</Typography>
          )}
        </DialogContent>
        <DialogActions>
          <Button
            onClick={() => {
              setDialogOpen(false);
              setCurrentService(null);
              setErrorMessage('');
            }}
            color="primary"
          >
            Cancelar
          </Button>
          <Button onClick={handleSave} color="primary">
            Guardar
          </Button>
        </DialogActions>
      </Dialog>
      <NotificationGreen
        open={notificationOpen}
        message={notificationMessage}
        onClose={() => setNotificationOpen(false)}
      />
    </Container>
  );
}

export default MyServices;
