import React, { useState } from 'react';
import {
  Container,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  Grid,
  Box,
} from '@mui/material';

function Comments() {
  const [comments, setComments] = useState([
    {
      id: 1,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 2,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 3,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 4,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 5,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 6,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 7,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
    {
      id: 8,
      user: 'Usuario 1',
      comment: 'Excelente servicio!',
      secondaryComment: 'Estoy muy satisfecho con la clase que tomé.',
    },
  ]);

  const handlePublish = (commentId) => {
    console.log(`Publishing comment with ID: ${commentId}`);
  };

  const handleDelete = (commentId) => {
    const updatedComments = comments.filter(
      (comment) => comment.id !== commentId
    );
    setComments(updatedComments);
  };

  return (
    <Container>
      <Box sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Comentarios
        </Typography>
        <List>
          {comments.map((comment) => (
            <ListItem key={comment.id} disablePadding>
              <Grid container spacing={2}>
                <Grid item xs={12} md={8}>
                  <ListItemText
                    primary={comment.user}
                    secondary={
                      <>
                        <Typography
                          component="span"
                          variant="body2"
                          color="textPrimary"
                          sx={{ display: 'block' }}
                        >
                          {comment.comment}
                        </Typography>
                        {comment.secondaryComment}
                      </>
                    }
                  />
                </Grid>
                <Grid item xs={12} md={2}>
                  <Button
                    variant="contained"
                    color="primary"
                    fullWidth
                    onClick={() => handlePublish(comment.id)}
                  >
                    Publicar
                  </Button>
                </Grid>
                <Grid item xs={12} md={2}>
                  <Button
                    variant="contained"
                    color="secondary"
                    fullWidth
                    onClick={() => handleDelete(comment.id)}
                  >
                    Borrar
                  </Button>
                </Grid>
              </Grid>
            </ListItem>
          ))}
        </List>
      </Box>
    </Container>
  );
}

export default Comments;
