import React from 'react';
import {
  Button,
  Container,
  Typography,
  Grid,
  Link,
  Avatar,
  Card,
  CardContent,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
} from '@mui/material';
import makeStyles from '@mui/styles/makeStyles';
import mockProvider from '../data/mockProvider';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  button: {
    margin: theme.spacing(1),
  },
  largeAvatar: {
    width: theme.spacing(10),
    height: theme.spacing(10),
  },
  card: {
    marginTop: theme.spacing(2),
  },
}));

function ProviderProfile() {
  const classes = useStyles();
  const [isEditing, setIsEditing] = useState(false);
  const [updatedProvider, setUpdatedProvider] = useState(mockProvider);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUpdatedProvider((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    // Aquí puedes actualizar mockProvider con la nueva información.
    // Por ahora, simplemente cerramos el formulario.
    setIsEditing(false);
  };

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom>
        Perfil del Proveedor
      </Typography>
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={12} sm={4} md={3}>
          <Avatar
            alt={`${mockProvider.firstName} ${mockProvider.lastName}`}
            src={mockProvider.profileImage}
            className={classes.largeAvatar}
          />
        </Grid>
        <Grid item xs={12} sm={8} md={9}>
          <Typography variant="h5">{`${mockProvider.firstName} ${mockProvider.lastName}`}</Typography>
          <Typography variant="subtitle1">{mockProvider.title}</Typography>
        </Grid>
      </Grid>
      <Card className={classes.card}>
        <CardContent>
          <Typography variant="h6">Sobre mí:</Typography>
          <Typography variant="body2">{mockProvider.experience}</Typography>
        </CardContent>
      </Card>
      <Grid container spacing={2}>
        <Grid item>
          <Button
            variant="contained"
            color="primary"
            className={classes.button}
            component={Link}
            href="/"
          >
            Volver al inicio
          </Button>
        </Grid>
      </Grid>
    </Container>
  );
}

export default ProviderProfile;
