import React, { useState } from 'react';
import { Container, Typography, Tabs, Tab } from '@mui/material';
import makeStyles from '@mui/styles/makeStyles';
// import HiringList from './HiringList';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  list: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));

function Hirings() {
  const classes = useStyles();
  const [currentTab, setCurrentTab] = useState('General');
  const [contrataciones, setContrataciones] = useState(contratacionesData);

  const handleChangeTab = (event, newValue) => {
    setCurrentTab(newValue);
  };

  const handleEstadoChange = (id, nuevoEstado) => {
    const updatedContrataciones = contrataciones.map((contratacion) => {
      if (contratacion.id === id) {
        return { ...contratacion, estado: nuevoEstado };
      }
      return contratacion;
    });
    setContrataciones(updatedContrataciones);
  };

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom>
        Contrataciones
      </Typography>
      <Tabs
        value={currentTab}
        onChange={handleChangeTab}
        centered
        variant="scrollable"
        scrollButtons="auto"
      >
        <Tab label="General" value="General" />
        <Tab label="Solicitada" value="solicitada" />
        <Tab label="Aceptada" value="aceptada" />
        <Tab label="Finalizada" value="finalizada" />
      </Tabs>
      <List className={classes.list}>
        {contrataciones
          .filter((c) => currentTab === 'General' || c.estado === currentTab)
          .map((contratacion) => (
            <ListItem key={contratacion.id}>
              <ListItemText
                primary={contratacion.servicio}
                secondary={
                  <>
                    Usuario: {contratacion.usuario} <br />
                    Tel√©fono de contacto: {contratacion.numeroTelefonico} <br />
                    Franja Horaria: {contratacion.horarioContacto} <br />
                    Estado: 
                    <Select
                      value={contratacion.estado}
                      onChange={(e) =>
                        handleEstadoChange(contratacion.id, e.target.value)
                      }
                    >
                      <MenuItem value="solicitada">Solicitada</MenuItem>
                      <MenuItem value="aceptada">Aceptada</MenuItem>
                      <MenuItem value="finalizada">Finalizada</MenuItem>
                    </Select>
                  </>
                }
              />
            </ListItem>
          ))}
      </List>
    </Container>
  );
}

export default Hirings;
