import React, { useState } from 'react';
import { Container, Typography, Tabs, Tab, Pagination } from '@mui/material';
import makeStyles from '@mui/styles/makeStyles';
import HiringList from './HiringList';
import mockHirings from '../../data/mockHirings';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  list: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));

function Hirings() {
  const classes = useStyles();

  const [contrataciones, setContrataciones] = useState(mockHirings);
  const [currentTab, setCurrentTab] = useState('General');

  const handleChangeTab = (event, newValue) => {
    setCurrentTab(newValue);
  };

  const handleEstadoChange = (id, nuevoEstado) => {
    const updatedContrataciones = contrataciones.map((contratacion) => {
      if (contratacion.id === id) {
        return { ...contratacion, estado: nuevoEstado };
      }
      return contratacion;
    });
    setContrataciones(updatedContrataciones);
  };

  return (
    <div>
      <Container className={classes.root}>
        <Typography variant="h4" gutterBottom>
          Contrataciones
        </Typography>

        <Tabs
          variant="fullWidth"
          size="small"
          value={currentTab}
          onChange={handleChangeTab}
          centered
          className={classes.tabs}
        >
          <Tab label="General" value="General" />
          <Tab label="Solicitada" value="solicitada" />
          <Tab label="Aceptada" value="aceptada" />
          <Tab label="Finalizada" value="finalizada" />
        </Tabs>

        <HiringList
          contrataciones={
            currentTab === 'General'
              ? contrataciones
              : contrataciones.filter((c) => c.estado === currentTab)
          }
          handleEstadoChange={handleEstadoChange}
          classes={classes}
        />
      </Container>
    </div>
  );
}

export default Hirings;
